WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
var ids = {!GETRECORDIDS($ObjectType.Contact)};
if (!ids || ids.length < 1)
    alert('Please select at least one record.');
else
{
    var url = "/apex/loop__masslooplus?retURL=" + encodeURI(window.location.pathname+window.location.search);
    url += "&recordIds="+ids.join(',');
    url += "&sessionId={!$Api.Session_ID}";
    window.location.href = url;
}
