if(records != null && records != ''){
mywin = window.open('/apex/activities__createTasks?Ids='+records+'&retURL='+returnURL+'&type=whoid'+'&SelectedObject=Contact' , '_top',
'height=400,width=600,status=yes,toolbar=no,menubar=no,location=no,center=yes,resizable=no');
mywin.focus();
}else{
alert('Please select atleast one record.');
}
