WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
var ids = {!GETRECORDIDS($ObjectType.Opportunity)};
if (!ids || ids.length < 1)
    alert('Please select at least one record.');
else
{
    var url = "/apex/loop__loopmerge?retURL="+encodeURI(window.location.pathname+window.location.search);
    url += "&recordIds="+ids.join(',');
    window.location.href = url;
}
